'use server';

import { cookies } from 'next/headers';

export async function updateTrainerOnboardingAction(data: any) {
    try {
        const cookieStore = await cookies();
        const accessToken = cookieStore.get('accessToken')?.value;

        if (!accessToken) {
            return { error: 'Unauthorized' };
        }

        const response = await fetch(`${process.env.NEXT_PUBLIC_APP_URL || 'http://localhost:3000'}/api/onboarding/trainer`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${accessToken}`,
            },
            body: JSON.stringify(data),
        });

        const result = await response.json();

        if (!response.ok) {
            return { error: result.message || 'Onboarding update failed' };
        }

        return { success: true, nextStep: result.nextStep };
    } catch (error) {
        console.error('Onboarding action error:', error);
        return { error: 'Something went wrong. Please try again.' };
    }
}

import { NextResponse } from 'next/server';
import { prisma } from '@/lib/prisma';
import { verifyPassword, generateAccessToken, generateRefreshToken, hashToken, REFRESH_TOKEN_EXPIRY_MS } from '@/lib/auth';
import { Role } from '@prisma/client';

export async function POST(request: Request) {
    try {
        const body = await request.json();
        const { email, phone, password } = body;

        let user = null;

        if (email) {
            user = await prisma.user.findUnique({ where: { email } });
        } else if (phone) {
            user = await prisma.user.findUnique({ where: { phone } });
        }

        if (!user) {
            return NextResponse.json(
                { success: false, message: 'User not found' },
                { status: 404 }
            );
        }

        // Verify Password (if user has one)
        if (user.passwordHash) {
            if (!password) {
                return NextResponse.json(
                    { success: false, message: 'Password required' },
                    { status: 400 }
                );
            }
            const isValid = await verifyPassword(password, user.passwordHash);
            if (!isValid) {
                return NextResponse.json(
                    { success: false, message: 'Invalid credentials' },
                    { status: 401 }
                );
            }
        } else {
            // If user has no password (maybe purely OTP based in future?), fail for now as we don't have OTP impl
            // Or if it's a client without password, we might need OTP flow here.
            // For this task, we assume password or we mock OTP.
            // Let's assume for now Clients also set a password during signup or we just fail.
            return NextResponse.json(
                { success: false, message: 'Authentication method not supported yet (no password set)' },
                { status: 401 }
            );
        }

        // Login successful - Generate Tokens
        const accessToken = await generateAccessToken({ userId: user.id, role: user.role });
        const refreshToken = generateRefreshToken();
        const refreshTokenHash = hashToken(refreshToken);

        // Save Refresh Token
        await prisma.refreshToken.create({
            data: {
                userId: user.id,
                tokenHash: refreshTokenHash,
                expiresAt: new Date(Date.now() + REFRESH_TOKEN_EXPIRY_MS),
            },
        });

        return NextResponse.json({
            success: true,
            message: 'Login successful',
            data: {
                user: {
                    id: user.id,
                    name: user.name,
                    email: user.email,
                    phone: user.phone,
                    role: user.role,
                },
                accessToken,
                refreshToken,
            }
        });

    } catch (error) {
        console.error('Login error:', error);
        return NextResponse.json(
            { success: false, message: 'Internal server error' },
            { status: 500 }
        );
    }
}

import { NextResponse } from 'next/server';
import { prisma } from '@/lib/prisma';
import { verifyAccessToken } from '@/lib/auth';
import { Role, LocationType } from '@prisma/client';

export async function POST(request: Request) {
    try {
        // 1. Verify Auth
        const authHeader = request.headers.get('Authorization');
        if (!authHeader?.startsWith('Bearer ')) {
            return NextResponse.json(
                { success: false, message: 'Unauthorized' },
                { status: 401 }
            );
        }
        const token = authHeader.split(' ')[1];
        const payload = await verifyAccessToken(token);

        if (!payload || payload.role !== Role.TRAINER) {
            return NextResponse.json(
                { success: false, message: 'Unauthorized or invalid role' },
                { status: 403 }
            );
        }

        const { userId } = payload;
        const body = await request.json();
        const { step, bio, location, country, state, city, zip, hasGym, specialties, services, photoUrl } = body;

        if (!step) {
            return NextResponse.json(
                { success: false, message: 'Step is required' },
                { status: 400 }
            );
        }

        // 2. Fetch current profile to calculate next step
        const currentProfile = await prisma.trainerProfile.findUnique({ where: { userId } });
        const currentOnboardingStep = currentProfile?.onboardingStep || 1;

        // 3. Update Profile & Relations based on Step
        await prisma.$transaction(async (tx) => {
            if (step === 1) {
                // Step 1: Bio
                await tx.trainerProfile.update({
                    where: { userId },
                    data: {
                        bio,
                        onboardingStep: Math.max(currentOnboardingStep, 2)
                    },
                });
            } else if (step === 2) {
                // Step 2: Location
                await tx.trainerProfile.update({
                    where: { userId },
                    data: {
                        location,
                        country,
                        state,
                        city,
                        zip,
                        onboardingStep: Math.max(currentOnboardingStep, 3)
                    },
                });
            } else if (step === 3) {
                // Step 3: Gym
                await tx.trainerProfile.update({
                    where: { userId },
                    data: { hasGym, onboardingStep: Math.max(currentOnboardingStep, 4) },
                });
            } else if (step === 4) {
                // Step 4: Speciality
                let specialtyIdsString = null;
                if (specialties && Array.isArray(specialties)) {
                    const specialtyIds = [];
                    for (const name of specialties) {
                        const slug = name.toLowerCase().trim().replace(/ /g, '-').replace(/[^\w-]/g, '');
                        const s = await tx.specialty.upsert({
                            where: { slug },
                            update: { name },
                            create: { name, slug }
                        });
                        specialtyIds.push(s.id);
                    }
                    specialtyIdsString = specialtyIds.join(', ');
                }
                await tx.trainerProfile.update({
                    where: { userId },
                    data: {
                        specialtyIds: specialtyIdsString,
                        onboardingStep: Math.max(currentOnboardingStep, 5)
                    },
                });
            } else if (step === 5) {
                // Step 5: Service
                if (services && Array.isArray(services)) {
                    const trainer = currentProfile;
                    if (trainer) {
                        await tx.trainerService.deleteMany({ where: { trainerId: trainer.id } });
                        await tx.trainerService.createMany({
                            data: services.map((s: any) => ({
                                trainerId: trainer.id,
                                title: s.title,
                                durationMinutes: parseInt(s.durationMinutes),
                                price: parseFloat(s.price),
                                description: s.description,
                                locationType: s.locationType as LocationType,
                            })),
                        });
                        await tx.trainerProfile.update({
                            where: { id: trainer.id },
                            data: { onboardingStep: Math.max(currentOnboardingStep, 6) },
                        });
                    }
                }
            } else if (step === 6) {
                // Step 6: Profile (Photo)
                await tx.trainerProfile.update({
                    where: { userId },
                    data: {
                        profileImage: photoUrl,
                        onboardingStep: Math.max(currentOnboardingStep, 7)
                    }, // 7 means complete
                });
            }
        });

        return NextResponse.json({
            success: true,
            message: `Step ${step} completed successfully`,
            nextStep: step + 1
        });

    } catch (error) {
        console.error('Trainer onboarding error:', error);
        return NextResponse.json(
            { success: false, message: 'Internal server error' },
            { status: 500 }
        );
    }
}

