'use client';

import { useState, useEffect } from 'react';
import { useUser } from '@/hooks/useUser';
import { useTrainerOnboarding } from '@/hooks/useOnboarding';
import { BioStep } from '@/components/onboarding/steps/BioStep';
import { LocationStep } from '@/components/onboarding/steps/LocationStep';
import { GymStep } from '@/components/onboarding/steps/GymStep';
import { SpecialityStep } from '@/components/onboarding/steps/SpecialityStep';
import { ServicesStep } from '@/components/onboarding/steps/ServicesStep';
import { PhotoStep } from '@/components/onboarding/steps/PhotoStep';
import { VerificationStep } from '@/components/onboarding/steps/VerificationStep';

export default function OnboardingPage() {
    const { data: user, isLoading } = useUser();
    const onboarding = useTrainerOnboarding();
    const [activeStep, setActiveStep] = useState<number | null>(null);

    // Initialize activeStep from user data on first load
    useEffect(() => {
        if (user && activeStep === null) {
            setActiveStep(user.onboardingStep || 1);
        }
    }, [user, activeStep]);

    const handleNext = (data: any) => {
        onboarding.mutate(data, {
            onSuccess: () => {
                if (activeStep !== null) {
                    setActiveStep(activeStep + 1);
                }
            }
        });
    };

    const handleBack = () => {
        if (activeStep && activeStep > 1) {
            setActiveStep(activeStep - 1);
        }
    };

    if (isLoading || activeStep === null) {
        return (
            <div className="w-full flex items-center justify-center p-20">
                <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-black"></div>
            </div>
        );
    }

    const renderStep = () => {
        const trainerProfile = user?.trainerProfile;

        switch (activeStep) {
            case 1:
                return (
                    <BioStep
                        data={trainerProfile}
                        onNext={handleNext}
                        isPending={onboarding.isPending}
                        error={onboarding.error}
                    />
                );
            case 2:
                return (
                    <LocationStep
                        data={trainerProfile}
                        onNext={handleNext}
                        onBack={handleBack}
                        isPending={onboarding.isPending}
                        error={onboarding.error}
                    />
                );
            case 3:
                return (
                    <GymStep
                        data={trainerProfile}
                        onNext={handleNext}
                        onBack={handleBack}
                        isPending={onboarding.isPending}
                        error={onboarding.error}
                    />
                );
            case 4:
                return (
                    <SpecialityStep
                        data={trainerProfile}
                        onNext={handleNext}
                        onBack={handleBack}
                        isPending={onboarding.isPending}
                        error={onboarding.error}
                    />
                );
            case 5:
                return (
                    <ServicesStep
                        data={trainerProfile}
                        onNext={handleNext}
                        onBack={handleBack}
                        isPending={onboarding.isPending}
                        error={onboarding.error}
                    />
                );
            case 6:
                return (
                    <PhotoStep
                        data={trainerProfile}
                        onNext={handleNext}
                        onBack={handleBack}
                        isPending={onboarding.isPending}
                        error={onboarding.error}
                    />
                );
            case 7:
                return <VerificationStep />;
            default:
                return null;
        }
    };

    return (
        <div className="flex flex-col items-center w-full flex-1">
            {activeStep < 6 && (
                <>
                    <h2 className="text-4xl font-black text-gray-900 mb-8 text-center tracking-tight">
                        Let&apos;s get you started
                    </h2>

                    {/* Progress Bar */}
                    <div className="flex space-x-3 mb-12">
                        {[1, 2, 3, 4, 5].map((step) => (
                            <div
                                key={step}
                                className={`h-1.5 w-12 rounded-full transition-colors duration-300 ${step <= activeStep ? 'bg-black' : 'bg-gray-200'
                                    }`}
                            />
                        ))}
                    </div>
                </>
            )}
            {renderStep()}
        </div>
    );
}

'use client';

import { useState, useEffect } from 'react';
import { TextArea } from '@/components/ui/TextArea';
import { OnboardingCard } from '@/components/onboarding/OnboardingCard';
import { Button } from '@/components/ui/Button';

interface BioStepProps {
    data?: any;
    onNext: (data: { step: number; bio: string }) => void;
    isPending: boolean;
    error: any;
}

export function BioStep({ data, onNext, isPending, error }: BioStepProps) {
    const [bio, setBio] = useState(data?.bio || '');
    const [localError, setLocalError] = useState('');

    const handleNext = () => {
        if (!bio.trim()) {
            setLocalError('Bio is required');
            return;
        }

        onNext({
            step: 1,
            bio: bio.trim()
        });
    };

    return (
        <div className="flex flex-col items-center w-full">
            <OnboardingCard>
                <div className="text-center mb-12">
                    <h3 className="text-2xl font-black text-gray-900 mb-2">
                        Tell us about yourself
                    </h3>
                    <p className="text-gray-500">
                        Please enter your journey (this will be shown on your profile)
                    </p>
                </div>

                <div className="w-full">
                    {error && (
                        <div className="mb-4 p-3 text-sm text-red-500 bg-red-50 rounded-lg border border-red-200">
                            {error.message || 'Something went wrong. Please try again.'}
                        </div>
                    )}
                    <TextArea
                        id="bio"
                        label="Bio"
                        placeholder="Enter here"
                        rows={6}
                        value={bio}
                        onChange={(e) => {
                            setBio(e.target.value);
                            setLocalError('');
                        }}
                        error={localError}
                    />
                </div>
            </OnboardingCard>

            <div className="w-full flex justify-end mt-[40px]">
                <Button
                    onClick={handleNext}
                    disabled={isPending}
                    className="bg-black hover:bg-gray-800 text-white w-full sm:w-[250px] h-[52px] rounded-full text-base font-bold"
                >
                    {isPending ? 'Saving...' : 'Next'}
                </Button>
            </div>
        </div>
    );
}

